const a=()=>{const c=document.getElementById("toggle-nav"),e=document.getElementById("header-nav"),t=document.getElementById("container-hero"),o=document.querySelectorAll(".header-link"),l={threshold:.8};c.addEventListener("click",()=>e.classList.toggle("menu-open"));const i=s=>{s.forEach(n=>{n.isIntersecting?e.classList.remove("header-off-top"):e.classList.add("header-off-top")})};new IntersectionObserver(i,l).observe(t),o.forEach(s=>{s.addEventListener("click",()=>{const n=s.getAttribute("href"),r=document.getElementById(n);e.classList.remove("menu-open"),r?r.scrollTo():window.location.href="../../"+n})})};a();class d extends HTMLElement{constructor(){super();const e=this.querySelector(".footer-list-header"),t=this.querySelector(".footer-list"),o=this.querySelector(".footer-list-sign");e.addEventListener("click",()=>{t.classList.toggle("footer-list-open"),t.classList.contains("footer-list-open")?o.textContent="-":o.textContent="+"})}}customElements.define("footer-list",d);
